mapper.select:
select savefolder, originfile, savefile
from file
where articleno = #{articleno}

select articleno, subject, content, regtime, count
from notice
where articleno = #{articleNo}

mapper.update:
update notice
set count = count +1
where articleno = #{articleNo}

update notice
set subject = #{subject}, content = #{content}
where articleno = #{articleNo}
